mapper.select:
SELECT DEPT_ID, NAME, ISUSE,ISNULL(IS_START,0) AS IS_START FROM DEPARTMENT
AND NAME LIKE '%' + #{name} + '%'
AND ISUSE = #{isuse}

SELECT DEPT.NAME,ISNULL(PA.ARREASE,0) AS ARREASE,ISNULL(PA.PRE_ARREASE,0) AS PRE_ARREASE FROM DEPARTMENT DEPT LEFT JOIN ( SELECT PTOTLE.DEPT_ID,PTOTLE.NAME, SUM(CASE WHEN PTOTLE.IS_ARREARSE=-1 THEN 1 ELSE 0 END ) AS ARREASE ,SUM(CASE WHEN PTOTLE.IS_ARREARSE=0 THEN 1 ELSE 0 END ) AS PRE_ARREASE FROM (SELECT PA.PATIENT_ID,PA.DEPT_ID,DEPT.NAME,MIN(PA.IS_ARREARSE) IS_ARREARSE FROM (SELECT DISTINCT PAYM.PATIENT_ID,PAYM.DEPT_ID ,CASE WHEN DATEDIFF(dd,PAYM.ENDTIME,PPM.END_DATE) >0 THEN -1 WHEN DATEDIFF(dd,PAYM.ENDTIME,PPM.END_DATE) <= 0 AND DATEDIFF(dd,PAYM.ENDTIME,PPM.END_DATE)>30 THEN 0 ELSE 1 END IS_ARREARSE FROM (SELECT PAY.PATIENT_ID,PAY.PAYSERVICE_ID,PAY.DEPT_ID ,MAX(PAY.ENDTIME)ENDTIME FROM PAYMENT PAY 1=1
AND PAY.DEPT_ID = #{deptId}
GROUP BY PAY.PATIENT_ID,PAY.PAYSERVICE_ID,PAY.DEPT_ID ) PAYM LEFT JOIN (SELECT PATIENT_ID,PAYSERVICE_ID,MAX(ISNULL(END_DATE,convert(varchar, getdate(), 110))) AS END_DATE FROM PA_PAYSERVICE GROUP BY PATIENT_ID,PAYSERVICE_ID) PPM ON PAYM.PATIENT_ID=PPM.PATIENT_ID AND PAYM.PAYSERVICE_ID=PPM.PAYSERVICE_ID UNION SELECT PME.PATIENT_ID,PME.DEPT_ID,CASE WHEN (PME.REAL_FEE+PME.ARREARS_FEE) < 0 THEN -1 WHEN (PME.REAL_FEE+PME.ARREARS_FEE)>=0 AND DATEDIFF(DD,convert(varchar, PME.END_DATE, 110),convert(varchar, getdate(), 110)) < 30 THEN 0 ELSE 1 END AS IS_ARREARSE FROM (SELECT P.PATIENT_ID,P.DEPT_ID,ISNULL(ME.REAL_FEE,0) AS REAL_FEE,ISNULL(ME.ARREARS_FEE,0) AS ARREARS_FEE,ME.BEG_DATE,ME.END_DATE FROM MEDICAL_EXPENSES ME LEFT JOIN PATIENT P ON P.HOSP_NUM=ME.HOSP_NUM 1=1
AND P.DEPT_ID = #{deptId}
)PME ) PA LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID=PA.DEPT_ID GROUP BY PA.PATIENT_ID,PA.DEPT_ID,DEPT.NAME) PTOTLE GROUP BY PTOTLE.DEPT_ID,PTOTLE.NAME ) PA ON PA.DEPT_ID=DEPT.DEPT_ID
