mapper.select:
SELECT DEPT_ID, NAME, ISUSE,ISNULL(IS_START,0) AS IS_START FROM DEPARTMENT
AND NAME LIKE '%' + #{name} + '%'
AND ISUSE = #{isuse}

SELECT DEPT.DEPT_ID, DEPT.NAME,ISNULL(PA.ARREASE,0) AS ARREASE,ISNULL(PA.PRE_ARREASE,0) AS PRE_ARREASE FROM DEPARTMENT DEPT LEFT JOIN ( SELECT PTOTLE.DEPT_ID,PTOTLE.NAME, SUM(CASE WHEN PTOTLE.IS_ARREARSE=-1 THEN 1 ELSE 0 END ) AS ARREASE ,SUM(CASE WHEN PTOTLE.IS_ARREARSE=0 THEN 1 ELSE 0 END ) AS PRE_ARREASE FROM (SELECT PA.PATIENT_ID,PA.DEPT_ID,DEPT.NAME,MIN(PA.IS_ARREARSE) IS_ARREARSE FROM (SELECT PAPM.PATIENT_ID,PAPM.DEPT_ID ,CASE WHEN DATEDIFF(dd,PAPM.ENDTIME,PAPM.END_DATE) >0 THEN -1 WHEN DATEDIFF(dd,PAPM.ENDTIME,PAPM.END_DATE)<=0 AND DATEDIFF(dd,PAPM.ENDTIME,PAPM.END_DATE)>-30 THEN 0 ELSE 1 END IS_ARREARSE FROM ( SELECT DISTINCT PPM.PATIENT_ID,PPM.DEPT_ID,PPM.BEG_DATE,PPM.END_DATE,ISNULL(PAYM.ENDTIME,PPM.BEG_DATE) AS ENDTIME FROM (SELECT PP.PATIENT_ID,PP.PAYSERVICE_ID,P.DEPT_ID,MIN(PP.BEG_DATE) AS BEG_DATE,MAX(ISNULL(PP.END_DATE,convert(varchar, getdate(), 110))) AS END_DATE FROM PA_PAYSERVICE PP LEFT JOIN PATIENT P ON P.PATIENT_ID=PP.PATIENT_ID 1=1
AND P.DEPT_ID IN
#{item}
GROUP BY PP.PATIENT_ID,PP.PAYSERVICE_ID,P.DEPT_ID) PPM LEFT JOIN (SELECT PAY.PATIENT_ID,PAY.PAYSERVICE_ID,MAX(PAY.ENDTIME)ENDTIME FROM PAYMENT PAY GROUP BY PAY.PATIENT_ID,PAY.PAYSERVICE_ID,PAY.DEPT_ID) PAYM ON PAYM.PATIENT_ID=PPM.PATIENT_ID AND PAYM.PAYSERVICE_ID=PPM.PAYSERVICE_ID)PAPM UNION SELECT PME.PATIENT_ID,PME.DEPT_ID,CASE WHEN (PME.REAL_FEE+PME.ARREARS_FEE) < 0 THEN -1 WHEN (PME.REAL_FEE+PME.ARREARS_FEE)>=0 AND DATEDIFF(DD,convert(varchar, PME.END_DATE, 110),convert(varchar, getdate(), 110)) < 30 THEN 0 ELSE 1 END AS IS_ARREARSE FROM (SELECT P.PATIENT_ID,P.DEPT_ID,ISNULL(ME.REAL_FEE,0) AS REAL_FEE,ISNULL(ME.ARREARS_FEE,0) AS ARREARS_FEE,ME.BEG_DATE,ME.END_DATE FROM MEDICAL_EXPENSES ME LEFT JOIN PATIENT P ON P.HOSP_NUM=ME.HOSP_NUM 1=1
AND P.DEPT_ID IN
#{item}
)PME ) PA LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID=PA.DEPT_ID GROUP BY PA.PATIENT_ID,PA.DEPT_ID,DEPT.NAME) PTOTLE GROUP BY PTOTLE.DEPT_ID,PTOTLE.NAME ) PA ON PA.DEPT_ID=DEPT.DEPT_ID
