mapper.select:
select ME.* ,P.NAME AS patientName ,P.SEX AS SEX ,P.AGE AS AGE ,DEPT.NAME AS DEPT_NAME ,P2.PATITYPENAME ,M.MITYPENAME ,E.EMP_NAME ,DEPT2.NAME AS execDeptName ,P3.MONEY AS marginAmount ,NOMEG.paCount AS paCount ,CASE WHEN P3.MONEY < 0 THEN 1 ELSE 2 END isArrearage from
( SELECT B.* FROM ( SELECT HOSP_NUM,MAX(CREATE_DATE) CREATE_DATE FROM HOSPITAL_PATIENT GROUP BY HOSP_NUM ) A,HOSPITAL_PATIENT B WHERE A.HOSP_NUM = B.HOSP_NUM AND A.CREATE_DATE = B.CREATE_DATE ) ME
HOSPITAL_PATIENT ME
LEFT JOIN PATIENT P ON P.HOSP_NUM = ME.HOSP_NUM LEFT JOIN PATIENTTYPE P2 ON P.PATITYPEID = P2.PATITYPEID LEFT JOIN PATINET_MARGIN P3 ON P3.HOSP_NUM = ME.HOSP_NUM LEFT JOIN ( SELECT C.ID_CARD AS ID_CARD, SUM( ( C.days + 1 ) * C.UNIT_PRICE ) AS paCount FROM ( SELECT A.HOSP_NUM AS HOSP_NUM, A.UNIT_PRICE AS UNIT_PRICE, A.PAYSERVICE_ID AS PAYSERVICE_ID, A.ID_CARD AS ID_CARD, A.BEG_DATE, A.END_DATE, CASE WHEN B.PAY_TYPE = 0 AND A.END_DATE IS NOT NULL THEN datediff( DAY, A.BEG_DATE, A.END_DATE ) WHEN B.PAY_TYPE = 0 AND A.END_DATE IS NULL THEN datediff( DAY, A.BEG_DATE, GETDATE ( ) ) WHEN B.PAY_TYPE = 1 THEN datediff( DAY, A.BEG_DATE, A.END_DATE ) ELSE datediff( DAY, A.BEG_DATE, GETDATE ( ) ) END AS days FROM PA_PAYSERVICE A LEFT JOIN PAYSERVICE B ON A.PAYSERVICE_ID = B.PAYSERVICE_ID ) C GROUP BY C.ID_CARD ) NOMEG ON P.ID_CARD = NOMEG.ID_CARD LEFT JOIN MEDICINSURTYPE M ON M.MITYPEID = P.MITYPEID LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID = ME.DEPT_ID LEFT JOIN DEPARTMENT DEPT2 ON DEPT2.DEPT_ID = ME.EXECUTE_DEPT LEFT JOIN EMPLOYEE E ON E.ID = ME.EMP_ID
AND ME.ID_CARD like '%'+ #{idCard}+'%'
AND ME.serial_number like '%'+ #{serialNumber}+'%'
AND ME.TYPE = #{type}
AND ME.HOSP_NUM = #{hospNum}
AND P.NAME LIKE '%' + #{patientName} + '%'
AND ME.DEPT_ID = #{deptId}
AND ME.DEPT_ID IN
#{item}
AND P.PATITYPEID = #{patiTypeId}
AND P.MITYPEID = #{miTypeId}
AND ME.EMP_ID = #{empId}
AND ME.IS_IN_HOSPITAL = #{isInHospital}
AND P3.MONEY < 0
AND P3.MONEY >= 0
AND CONVERT(varchar(100), ME.CREATE_DATE, 23) >= CONVERT(varchar(100), #{begCreateDate}, 23)
AND CONVERT(varchar(100), ME.CREATE_DATE, 23) <= CONVERT(varchar(100), #{endCreateDate}, 23)
AND CONVERT(varchar(100), ME.END_DATE, 23) >= CONVERT(varchar(100), #{begDate}, 23)
AND CONVERT(varchar(100), ME.END_DATE, 23) <= CONVERT(varchar(100), #{endDate}, 23)
order by ME.CREATE_DATE DESC
