mapper.select:
SELECT DISTINCT ME.ME_ID ,IS_IN_HOSPITAL ,REMARKS ,ME.HOSP_NUM ,ME.DEPOSIT_FEE ,ME.REAL_FEE ,ME.DEPOSIT_FEE + ME.REAL_FEE arrears ,ME.ARREARS_FEE ARREARS_FEE_BACK ,SP.MONEY ARREARS_FEE ,ISNULL(ME.REAL_FEE,0) + ISNULL(ME.DEPOSIT_FEE,0) + ISNULL(SP.MONEY,0) spMoney ,ME.CREATE_DATE ,ME.BEG_DATE ,ME.END_DATE ,ME.DAYS ,DATEDIFF(DAY,ME.END_DATE ,GETDATE()) DAY ,ME.ACCOUNT_ID ,ME.ESTIMATED_ARREARS ,P.NAME AS PATIENT_NAME ,DEPT.NAME AS DEPT_NAME ,CASE WHEN IS_IN_HOSPITAL = 1 THEN '在院' ELSE '出院' END isInHospitalLabel ,P.PATITYPEID ,P2.PATITYPENAME ,P.MITYPEID ,M.MITYPENAME ,P.IN_HOSP ,ME.EMP_ID ,E.EMP_NAME FROM
( SELECT B.* FROM ( SELECT HOSP_NUM,MAX(CREATE_DATE) CREATE_DATE FROM MEDICAL_EXPENSES GROUP BY HOSP_NUM ) A,MEDICAL_EXPENSES B WHERE A.HOSP_NUM = B.HOSP_NUM AND A.CREATE_DATE = B.CREATE_DATE ) ME
MEDICAL_EXPENSES ME
LEFT JOIN PATIENT P ON P.HOSP_NUM = ME.HOSP_NUM LEFT JOIN PATIENTTYPE P2 ON P.PATITYPEID = P2.PATITYPEID LEFT JOIN MEDICINSURTYPE M ON M.MITYPEID = P.MITYPEID LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID = ME.DEPT_ID LEFT JOIN EMPLOYEE E ON E.ID = ME.EMP_ID LEFT JOIN ( SELECT ME_ID ,SUM(MONEY) MONEY FROM SUPPLEMENTARY_PAYMENT
AND CREATE_DATE >= #{spBegDate}
AND CREATE_DATE < #{spEndDate}
GROUP BY ME_ID ) SP ON SP.ME_ID = ME.ME_ID
AND ME.HOSP_NUM = #{hospNum}
AND P.NAME LIKE '%' + #{patientName} + '%'
AND ME.DEPT_ID = #{deptId}
AND ME.DEPT_ID IN
#{item}
AND P.PATITYPEID = #{patiTypeId}
AND ME.CREATE_DATE >= #{begCreateDate}
AND ME.CREATE_DATE <= #{endCreateDate}
AND ME.END_DATE >= #{begDate}
AND ME.END_DATE <= #{endDate}
AND P.MITYPEID = #{miTypeId}
AND P.IN_HOSP = #{inHosp}
AND ME.EMP_ID = #{empId}
AND ME.DEPOSIT_FEE + ME.REAL_FEE + ISNULL( SP.MONEY, 0 ) >= 0
AND ME.DEPOSIT_FEE + ME.REAL_FEE + ISNULL( SP.MONEY, 0 ) < 0
ORDER BY ME.CREATE_DATE DESC

SELECT A.PAYMENTTIME,SUM(A.DEPOSIT_FEE) as ACTUALPAYMENT
FROM
( SELECT CONVERT ( VARCHAR ( 100 ), CREATE_DATE, 111 ) AS PAYMENTTIME, DEPOSIT_FEE FROM MEDICAL_EXPENSES
where 1=1

    and HOSP_NUM = #{hospNum}


    and CONVERT(varchar(4), CREATE_DATE, 121) = #{paymenttime}

) A
GROUP BY
A.PAYMENTTIME
