mapper.select:
SELECT PAY.*,PP.PP_ID, isnull(PP.IS_USE,0) IS_USE, PP.BEG_DATE, PAY.NAME, PAY.PAY_TYPE, PAY.IN_HOSP, CASE WHEN PP.BEG_DATE IS NOT NULL AND PP.END_DATE IS NULL THEN DATEDIFF(DAY,PP.BEG_DATE , GETDATE())+1 WHEN PP.BEG_DATE IS NOT NULL AND PP.END_DATE IS NOT NULL THEN DATEDIFF(DAY,PP.BEG_DATE , PP.END_DATE)+1 WHEN PP.BEG_DATE IS NULL AND PP.END_DATE IS NULL THEN 0 ELSE 0 END DAY_NUM, PP.END_DATE, CASE WHEN PP.UNIT_PRICE IS NULL OR PP.UNIT_PRICE = 0 THEN PAY.PRICE ELSE PP.UNIT_PRICE END UNIT_PRICE, isnull(PP.PRE_RECEIVE,0) PRE_RECEIVE FROM (select PA.PATIENT_ID,PA.HOSP_NUM,PAY.PAYSERVICE_ID,PAY.NAME,PAY.PAY_TYPE,PAY.PRICE,PA.IN_HOSP from PATIENT PA,PAYSERVICE PAY WHERE 1=1 AND PAY.ISUSE=1
AND PA.PATIENT_ID=#{patientId}
)PAY LEFT JOIN (SELECT APP.* FROM (SELECT DISTINCT PATIENT_ID,PAYSERVICE_ID,MAX(BEG_DATE) BEG_DATE FROM PA_PAYSERVICE GROUP BY PATIENT_ID,PAYSERVICE_ID) MAXPP LEFT JOIN PA_PAYSERVICE APP ON MAXPP.PATIENT_ID=APP.PATIENT_ID AND MAXPP.PAYSERVICE_ID=APP.PAYSERVICE_ID AND MAXPP.BEG_DATE=APP.BEG_DATE
WHERE APP.PATIENT_ID=#{patientId}
) PP ON PAY.PAYSERVICE_ID=PP.PAYSERVICE_ID

mapper.update:
update PA_PAYSERVICE
set



            IS_USE=0,
            END_DATE=convert(varchar, getdate(), 110),


            IS_USE=1,



        UNIT_PRICE=#{unitPrice},


        PRE_RECEIVE=#{preReceive},


where 1=1

    AND PP_ID=#{ppId}


    AND PATIENT_ID=#{patientId}


    AND HOSP_NUM=#{hospNum}


    AND PAYSERVICE_ID=#{payserviceId}

update PA_PAYSERVICE
set IS_USE=1, END_DATE=NULL

    ,UNIT_PRICE=#{unitPrice}

where 1=1
and PATIENT_ID IN (SELECT PATIENT_ID FROM PATIENT T WHERE T.IN_HOSP = 1 )

    AND PP_ID=#{ppId}


    AND PATIENT_ID=#{patientId}


    AND HOSP_NUM=#{hospNum}

update PA_PAYSERVICEset
    IS_USE=0,
    END_DATE=#{now}
where
HOSP_NUM=#{hospNum}

mapper.select:
select * from PA_PAYSERVICE
where 1=1

   and PAYSERVICE_ID = #{paPayserviceBean.payserviceId}


    and PATIENT_ID = #{paPayserviceBean.patientId}

select * from PA_PAYSERVICE where
PATIENT_ID = #{patientId}
and PAYSERVICE_ID = #{proId}
and (
CONVERT(varchar(100), BEG_DATE, 23) >= CONVERT(varchar(100), #{startDate}, 23)
 and CONVERT(varchar(100), #{endDate}, 23) >= CONVERT(varchar(100), BEG_DATE, 23)
or CONVERT(varchar(100), END_DATE, 23) >= CONVERT(varchar(100), #{startDate}, 23)
 and CONVERT(varchar(100), #{endDate}, 23) >= CONVERT(varchar(100), END_DATE, 23)
)

select * from PA_PAYSERVICE where
PATIENT_ID = #{patientId}
and PAYSERVICE_ID = #{proId}
and (
CONVERT(varchar(100), #{startDate}, 23) >= CONVERT(varchar(100), BEG_DATE, 23)
 and CONVERT(varchar(100), END_DATE, 23) > CONVERT(varchar(100), #{startDate}, 23)
)

select *
from PA_PAYSERVICE
where HOSP_NUM = #{hospNum}
and PAYSERVICE_ID = #{payserviceId}
and END_DATE >= #{endDate}
and #{endDate} >= BEG_DATE

select *
from PA_PAYSERVICE
where HOSP_NUM = #{hospNum}
and PAYSERVICE_ID = #{payserviceId}
