mapper.select:
SELECT A.PATIENT_ID, A.HOSP_NUM, A.NAME, A.SEX, A.AGE,A.IS_NO_FUNDING, A.HOSP_TIME,A.PATITYPEID,PATIENTTYPE.PATITYPENAME,ACC.NAME AS ACCOUNT_NAME, A.IN_HOSP, CASE WHEN A.IN_HOSP=1 THEN null ELSE ISNULL(A.OUT_HOSP,null) END AS OUT_HOSP, A.DEPT_ID, A.RECEIVABLE, A.UPDATE_TIME,B.NAME DEPTNAME,A.MITYPEID,MEDICINSURTYPE.MITYPENAME, A.EMP_ID,E.emp_name as empName,ISNULL(PM.MONEY,0) AS money, A.ID_CARD,A.HOSP_BALANCE,A.LIAISON_MAN,A.LIAISON_MAN_PHONE,A.PATIENT_PHONE,A.UNIT_NAME, A.UNIT_ADDRESS,A.SOURCE,A.CREATE_DATE,A.CREATOR,A.REVISER,A.FLAG,D.TOTAL * (-1) as medicalTatol FROM PATIENT A LEFT JOIN DEPARTMENT B ON B.DEPT_ID = A.DEPT_ID LEFT JOIN PATIENTTYPE ON PATIENTTYPE.PATITYPEID = A.PATITYPEID LEFT JOIN MEDICINSURTYPE ON MEDICINSURTYPE.MITYPEID = A.MITYPEID LEFT JOIN employee E ON A.EMP_ID = E.id LEFT JOIN sys_account ACC ON ACC.SA_ID = A.ACCOUNT_ID LEFT JOIN PATINET_MARGIN PM ON PM.HOSP_NUM = A.HOSP_NUM LEFT JOIN ( SELECT T.PATIENT_ID, SUM ( T.DAYS ) DAYS, MAX ( T.UNIT_PRICE ) PRICE, SUM ( T.TOTAL ) TOTAL, MAX ( T.ENDDATE ) ENDTIME FROM ( SELECT PA.PATIENT_ID, PA.PAYSERVICE_ID, PA.BEG_DATE, PA.END_DATE, P.ENDTIME, CASE
WHEN P.PATIENT_ID IS NULL THEN DATEDIFF( DAY, PA.BEG_DATE, CASE WHEN PA.END_DATE IS NULL THEN GETDATE() ELSE PA.END_DATE END ) ELSE ( CASE
WHEN P.ENDTIME > PA.END_DATE THEN DATEDIFF( DAY, P.ENDTIME, PA.END_DATE ) ELSE ( CASE
WHEN PA.BEG_DATE IS NOT NULL AND PA.END_DATE IS NOT NULL AND PA.BEG_DATE > P.ENDTIME THEN DATEDIFF( DAY, PA.BEG_DATE, PA.END_DATE ) ELSE DATEDIFF( DAY, P.ENDTIME, CASE WHEN PA.END_DATE IS NULL THEN GETDATE() ELSE PA.END_DATE END ) END ) END ) END DAYS, PA.UNIT_PRICE, CASE
WHEN P.PATIENT_ID IS NULL THEN DATEDIFF( DAY, PA.BEG_DATE, CASE WHEN PA.END_DATE IS NULL THEN GETDATE() ELSE PA.END_DATE END ) ELSE ( CASE
WHEN P.ENDTIME > PA.END_DATE THEN DATEDIFF( DAY, P.ENDTIME, PA.END_DATE ) ELSE ( CASE WHEN PA.BEG_DATE IS NOT NULL AND PA.END_DATE IS NOT NULL AND PA.BEG_DATE > P.ENDTIME THEN DATEDIFF( DAY, PA.BEG_DATE, PA.END_DATE ) ELSE DATEDIFF( DAY, P.ENDTIME, CASE WHEN PA.END_DATE IS NULL THEN GETDATE() ELSE PA.END_DATE END ) END ) END ) END * PA.UNIT_PRICE TOTAL, CASE
WHEN P.PATIENT_ID IS NULL THEN CASE
WHEN PA.END_DATE IS NULL THEN GETDATE() ELSE PA.END_DATE END ELSE ( CASE
WHEN PA.BEG_DATE IS NOT NULL AND PA.END_DATE IS NOT NULL AND PA.BEG_DATE > P.ENDTIME THEN DATEADD( dd, DATEDIFF( DAY, PA.END_DATE, PA.BEG_DATE ), GETDATE()) ELSE ( CASE
WHEN P.ENDTIME > ( CASE WHEN PA.END_DATE IS NULL THEN GETDATE() ELSE PA.END_DATE END ) THEN P.ENDTIME END ) END ) END ENDDATE FROM PA_PAYSERVICE PA LEFT JOIN ( SELECT PAY.PATIENT_ID, PAY.PAYMENTTIME, PAY.PAYSERVICE_ID, CASE
WHEN ARREARS.PAYMENTTIME > PAY.PAYMENTTIME THEN DATEADD( DD, - 1, ARREARS.BEGTIME ) ELSE PAY.ENDTIME END ENDTIME FROM ( SELECT A.* FROM PAYMENT A, ( SELECT PATIENT_ID, PAYSERVICE_ID, MAX ( PAYMENTTIME ) PAYMENTTIME FROM PAYMENT WHERE ISUSE = 1 AND PAYMENT_STATUS = 0 GROUP BY PATIENT_ID, PAYSERVICE_ID ) B WHERE A.PATIENT_ID = B.PATIENT_ID AND A.PAYMENTTIME = B.PAYMENTTIME AND A.PAYSERVICE_ID = B.PAYSERVICE_ID AND A.ISUSE = 1 ) PAY LEFT JOIN ( SELECT E.* FROM PAYMENT E, ( SELECT PATIENT_ID, PAYSERVICE_ID, MAX ( PAYMENTTIME ) PAYMENTTIME FROM PAYMENT WHERE PAYMENT_STATUS = 1 AND ISUSE = 1 GROUP BY PATIENT_ID, PAYSERVICE_ID ) F WHERE E.PATIENT_ID = F.PATIENT_ID AND E.PAYMENTTIME = F.PAYMENTTIME AND E.PAYSERVICE_ID = F.PAYSERVICE_ID AND E.ISUSE = 1 ) ARREARS ON PAY.PATIENT_ID = ARREARS.PATIENT_ID AND PAY.PAYSERVICE_ID = ARREARS.PAYSERVICE_ID ) P ON PA.PATIENT_ID = P.PATIENT_ID AND PA.PAYSERVICE_ID = P.PAYSERVICE_ID ) T GROUP BY T.PATIENT_ID ) D ON A.PATIENT_ID = D.PATIENT_ID
1 = 1
