SELECT PAT.ENDDATE,PAT.DATEDIFFRENT,PT.PATITYPENAME,MT.MITYPENAME,DEPT.NAME AS deptName,P.* FROM (SELECT PM.PATIENT_ID,MIN(PM.ENDTIME) AS ENDDATE,DATEDIFF(DAY,GETDATE(),MIN(PM.ENDTIME)) AS DATEDIFFRENT FROM PATIENT P LEFT JOIN PAYMENT PM ON PM.PATIENT_ID= P.PATIENT_ID WHERE PM.PATIENT_ID IS NOT NULL AND PM.BEGTIME IS NOT NULL GROUP BY PM.PATIENT_ID) PAT LEFT JOIN PATIENT P ON PAT.PATIENT_ID=P.PATIENT_ID LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID=P.DEPT_ID LEFT JOIN PATIENTTYPE PT ON P.PATITYPEID=PT.PATITYPEID LEFT JOIN MEDICINSURTYPE MT ON MT.MITYPEID=P.MITYPEID
AND P.HOSP_NUM LIKE '%'+#{hospNum}+'%'
AND P.DEPT_ID = #{deptId}
AND P.NAME LIKE '%'+#{name}+'%'
AND PT.PATITYPEID = #{patitypeid}
AND P.PATITYPEID = #{patientId}
AND MT.MITYPEID = #{mitypeid}
