mapper.select:
SELECT A.PATIENT_ID, A.HOSP_NUM, A.NAME, A.SEX, A.AGE, A.HOSP_TIME,A.PATITYPEID,PATIENTTYPE.PATITYPENAME,ACC.NAME AS ACCOUNT_NAME, A.IN_HOSP, CASE WHEN A.IN_HOSP=1 THEN null ELSE ISNULL(A.OUT_HOSP,null) END AS OUT_HOSP, A.DEPT_ID, A.RECEIVABLE, A.UPDATE_TIME,B.NAME DEPTNAME,A.MITYPEID,MEDICINSURTYPE.MITYPENAME, A.EMP_ID,E.emp_name as empName,ISNULL(PM.MONEY,0) - ISNULL(NOMEG.paCount,0) AS money, A.ID_CARD,A.HOSP_BALANCE,A.LIAISON_MAN,A.LIAISON_MAN_PHONE,A.PATIENT_PHONE,A.UNIT_NAME, A.UNIT_ADDRESS,A.SOURCE,A.CREATE_DATE,A.CREATOR,A.REVISER,A.FLAG FROM PATIENT A LEFT JOIN DEPARTMENT B ON B.DEPT_ID = A.DEPT_ID LEFT JOIN PATIENTTYPE ON PATIENTTYPE.PATITYPEID = A.PATITYPEID LEFT JOIN MEDICINSURTYPE ON MEDICINSURTYPE.MITYPEID = A.MITYPEID LEFT JOIN employee E ON A.EMP_ID = E.id LEFT JOIN sys_account ACC ON ACC.SA_ID = A.ACCOUNT_ID LEFT JOIN PATINET_MARGIN PM ON PM.HOSP_NUM = A.HOSP_NUM LEFT JOIN ( SELECT NOMOUNT.ID_CARD AS ID_CARD, SUM( ( NOMOUNT.days + 1 ) * NOMOUNT.UNIT_PRICE ) AS paCount FROM ( SELECT SS.HOSP_NUM AS HOSP_NUM, SS.UNIT_PRICE AS UNIT_PRICE, SS.PAYSERVICE_ID AS PAYSERVICE_ID, SS.ID_CARD AS ID_CARD, SS.BEG_DATE, SS.END_DATE, CASE
WHEN S.PAY_TYPE = 0 AND SS.END_DATE IS NOT NULL THEN datediff( DAY, SS.BEG_DATE, SS.END_DATE ) WHEN S.PAY_TYPE = 0 AND SS.END_DATE IS NULL THEN datediff( DAY, SS.BEG_DATE, GETDATE ( ) ) WHEN S.PAY_TYPE = 1 THEN datediff( DAY, SS.BEG_DATE, SS.END_DATE ) ELSE datediff( DAY, SS.BEG_DATE, GETDATE ( ) ) END AS days FROM PA_PAYSERVICE SS LEFT JOIN PAYSERVICE S ON SS.PAYSERVICE_ID = S.PAYSERVICE_ID WHERE SS.IS_USE != 2 ) NOMOUNT GROUP BY NOMOUNT.ID_CARD ) NOMEG ON A.ID_CARD = NOMEG.ID_CARD
1 = 1
AND A.DEPT_ID = #{patientBean.deptId}
AND A.DEPT_ID IN
#{item}
AND A.NAME LIKE '%' + #{patientBean.name} + '%'
AND A.PATIENT_ID = #{patientBean.patientId}
AND A.EMP_ID = #{patientBean.empId}
AND A.HOSP_NUM like '%' + #{patientBean.hospNum} + '%'
AND A.MITYPEID = #{patientBean.mitypeid}
AND A.PATITYPEID = #{patientBean.patitypeid}
AND A.IN_HOSP = #{patientBean.inHosp}
AND A.SEX = #{patientBean.sex}
AND A.HOSP_TIME >= #{patientBean.begHospTime}
AND A.HOSP_TIME < #{patientBean.endHospTime}
AND A.EMP_ID = #{patientBean.empId}
AND A.ID_CARD LIKE '%' + #{patientBean.idCard} + '%'
AND A.LIAISON_MAN_PHONE LIKE '%' + #{patientBean.liaisonManPhone} + '%'

select * from PATIENT



            AND PATIENT_ID=#{patientId}


            AND HOSP_NUM=#{hospNum}

SELECT PAT.ENDDATE,PAT.DATEDIFFRENT,PT.PATITYPENAME,MT.MITYPENAME,DEPT.NAME AS deptName,P.* FROM (SELECT PM.PATIENT_ID,MIN(PM.ENDTIME) AS ENDDATE,DATEDIFF(DAY,GETDATE(),MIN(PM.ENDTIME)) AS DATEDIFFRENT FROM PATIENT P LEFT JOIN PAYMENT PM ON PM.PATIENT_ID= P.PATIENT_ID WHERE PM.PATIENT_ID IS NOT NULL AND PM.BEGTIME IS NOT NULL GROUP BY PM.PATIENT_ID) PAT LEFT JOIN PATIENT P ON PAT.PATIENT_ID=P.PATIENT_ID LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID=P.DEPT_ID LEFT JOIN PATIENTTYPE PT ON P.PATITYPEID=PT.PATITYPEID LEFT JOIN MEDICINSURTYPE MT ON MT.MITYPEID=P.MITYPEID
AND P.HOSP_NUM LIKE '%'+#{hospNum}+'%'
AND P.DEPT_ID = #{deptId}
AND P.NAME LIKE '%'+#{name}+'%'
AND PT.PATITYPEID = #{patitypeid}
AND P.PATITYPEID = #{patientId}
AND MT.MITYPEID = #{mitypeid}

