mapper.select:
select ME.* ,P.ID_CARD AS 'idCard' ,P.NAME AS patientName ,P.SEX AS SEX ,P.AGE AS AGE ,DEPT.NAME AS DEPT_NAME ,P2.PATITYPENAME ,M.MITYPENAME ,E.EMP_NAME ,P3.MONEY AS marginAmount ,CASE WHEN P3.MONEY < 0 THEN 1 ELSE 2 END isArrearage from PAYMENT_DETAILS ME LEFT JOIN PATIENT P ON P.HOSP_NUM = ME.HOSP_NUM LEFT JOIN PATIENTTYPE P2 ON P.PATITYPEID = P2.PATITYPEID LEFT JOIN PATINET_MARGIN P3 ON P3.HOSP_NUM = ME.HOSP_NUM LEFT JOIN MEDICINSURTYPE M ON M.MITYPEID = P.MITYPEID LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID = ME.DEPT_ID LEFT JOIN EMPLOYEE E ON E.ID = ME.EMP_ID
AND ME.TYPE = #{type}
AND P.ID_CARD like '%' + #{idCard} + '%'
AND ME.payment = #{payment}
AND ME.serial_number like '%' + #{serialNumber} + '%'
AND ME.HOSP_NUM like '%' + #{hospNum}+ '%'
AND P.NAME LIKE '%' + #{patientName} + '%'
AND ME.DEPT_ID = #{deptId}
AND ME.DEPT_ID IN
#{item}
AND P.PATITYPEID = #{patiTypeId}
AND P.MITYPEID = #{miTypeId}
AND ME.EMP_ID = #{empId}
AND ME.IS_IN_HOSPITAL = #{isInHospital}
AND P3.MONEY < 0
AND P3.MONEY >= 0
AND CONVERT(varchar(100), ME.CREATE_DATE, 23) >= CONVERT(varchar(100), #{begDate}, 23)
AND CONVERT(varchar(100), #{endDate}, 23) >= CONVERT(varchar(100), ME.CREATE_DATE, 23)
AND CONVERT(varchar(100), ME.END_DATE, 23) >= CONVERT(varchar(100), #{begCreateDate}, 23)
AND CONVERT(varchar(100), endCreateDate, 23) >= CONVERT(varchar(100), ME.END_DATE, 23)
AND CONVERT(varchar(100), P.HOSP_TIME, 23) >= CONVERT(varchar(100), hosBegDate, 23)
AND CONVERT(varchar(100), #{hosEndDate}, 23) >= CONVERT(varchar(100), P.HOSP_TIME, 23)
order by ME.PD_ID ASC
order by ME.CREATE_DATE DESC

SELECT MEG.ID_CARD, MEG.MARGIN AS MARGIN_USE, MEG.PATIENT AS PATIENT_USE, MEG.HOSPITAL AS HOSPITAL_USE, P.HOSP_NUM as hospNum, P.NAME AS patientName, P.SEX AS SEX, P.AGE AS AGE, P.IN_HOSP AS IN_HOSP, P.HOSP_TIME AS CREATE_DATE, P2.PATITYPENAME, M.MITYPENAME, DEPT.NAME AS DEPT_NAME, E.EMP_NAME, NOMEG.paCount AS noMeUse, MEG.MARGIN-MEG.HOSPITAL-MEG.PATIENT- ISNULL(NOMEG.paCount,0) AS MARGIN_AMOUNT, CASE WHEN MEG.MARGIN-MEG.HOSPITAL-MEG.PATIENT-NOMEG.paCount < 0 THEN 1 ELSE 2 END IS_ARREARGE FROM ( SELECT ME.ID_CARD, SUM ( CASE WHEN ME.MARGIN_TYPE = 1 THEN ISNULL( ME.MARGIN_USE, 0 ) ELSE -1*ISNULL( ME.MARGIN_USE, 0 ) END ) MARGIN, SUM ( CASE WHEN ME.MARGIN_TYPE = 1 THEN ISNULL( ME.HOSPITAL_USE, 0 ) ELSE -1*ISNULL( ME.HOSPITAL_USE, 0 ) END ) HOSPITAL, SUM ( CASE WHEN ME.MARGIN_TYPE = 1 THEN ISNULL( ME.PATIENT_USE, 0 ) ELSE -1*ISNULL( ME.PATIENT_USE, 0 ) END ) PATIENT FROM PAYMENT_DETAILS ME
AND ME.CREATE_DATE <= #{endDate}
AND #{begDate} <= ME.CREATE_DATE
GROUP BY ME.ID_CARD ) MEG LEFT JOIN PATIENT P ON P.ID_CARD = MEG.ID_CARD LEFT JOIN PATIENTTYPE P2 ON P.PATITYPEID = P2.PATITYPEID LEFT JOIN ( SELECT C.ID_CARD AS ID_CARD, SUM( ( C.days + 1 ) * C.UNIT_PRICE ) AS paCount FROM ( SELECT A.HOSP_NUM AS HOSP_NUM, A.UNIT_PRICE AS UNIT_PRICE, A.PAYSERVICE_ID AS PAYSERVICE_ID, A.ID_CARD AS ID_CARD, A.BEG_DATE, A.END_DATE, CASE
WHEN B.PAY_TYPE = 0 AND A.END_DATE IS NOT NULL THEN datediff( DAY, A.BEG_DATE, A.END_DATE ) WHEN B.PAY_TYPE = 0 AND A.END_DATE IS NULL THEN datediff( DAY, A.BEG_DATE, GETDATE ( ) ) WHEN B.PAY_TYPE = 1 THEN datediff( DAY, A.BEG_DATE, A.END_DATE ) ELSE datediff( DAY, A.BEG_DATE, GETDATE ( ) ) END AS days FROM PA_PAYSERVICE A LEFT JOIN PAYSERVICE B ON A.PAYSERVICE_ID = B.PAYSERVICE_ID
AND A.CREATE_DATE <= #{endDate}
AND #{begDate} <= A.CREATE_DATE
) C GROUP BY C.ID_CARD ) NOMEG ON MEG.ID_CARD = NOMEG.ID_CARD LEFT JOIN MEDICINSURTYPE M ON M.MITYPEID = P.MITYPEID LEFT JOIN DEPARTMENT DEPT ON DEPT.DEPT_ID = P.DEPT_ID LEFT JOIN EMPLOYEE E ON E.ID = P.EMP_ID
AND P.HOSP_NUM LIKE '%' + #{hospNum} + '%'
AND MEG.ID_CARD LIKE '%' + #{idCard} + '%'
AND P.NAME LIKE '%' + #{patientName} + '%'
AND P.DEPT_ID = #{deptId}
AND P.DEPT_ID IN
#{item}
AND P.PATITYPEID = #{patiTypeId}
AND P.MITYPEID = #{miTypeId}
AND P.EMP_ID = #{empId}
AND P.IN_HOSP = #{isInHospital}
AND MEG.MARGIN-MEG.HOSPITAL-MEG.PATIENT-NOMEG.paCount < 0
AND MEG.MARGIN-MEG.HOSPITAL-MEG.PATIENT-NOMEG.paCount >= 0
AND CONVERT(varchar(100), P.HOSP_TIME, 23) >= CONVERT(varchar(100), #{hosBegDate}, 23)
AND CONVERT(varchar(100), P.HOSP_TIME, 23) <= CONVERT(varchar(100), #{hosEndDate}, 23)
order by P.HOSP_TIME DESC

select count(*)
from PAYMENT_DETAILS where
CONVERT(varchar(100), pay_date, 23) = #{now}
and TYPE = #{type}

SELECT charge.* from ( SELECT ME.MARGIN_USE AS MARGIN_USE, ME.PATIENT_USE AS PATIENT_USE, ME.HOSPITAL_USE AS HOSPITAL_USE, ME.CURRENT_MARGIN AS CURRENT_MARGIN, ME.pay_date AS payDate, ME.ID_CARD AS idCard, ME.HOSP_NUM AS hospNum, ME.IS_IN_HOSPITAL AS isInHospital, P.NAME AS patientName, DEPT.NAME AS deptName, E.EMP_NAME AS empName FROM PAYMENT_DETAILS ME LEFT JOIN PATIENT P ON ME.ID_CARD = P.ID_CARD LEFT JOIN DEPARTMENT DEPT ON ME.DEPT_ID = DEPT.DEPT_ID LEFT JOIN EMPLOYEE E ON ME.EMP_ID = E.ID
AND ME.ID_CARD = #{idCard}
AND ME.DEPT_ID = #{deptId}
AND CONVERT(varchar(100), ME.pay_date, 23) >= CONVERT(varchar(100), #{startDate}, 23)
AND CONVERT(varchar(100), #{endDate}, 23) >= CONVERT(varchar(100), ME.pay_date, 23)
UNION ALL SELECT NULL AS MARGIN_USE, NULL AS PATIENT_USE, NULL HOSPITAL_USE, balance AS CURRENT_MARGIN, pay_date AS payDate, id_card AS idCard, hosp_num AS hospNum, is_hosp AS isInHospital, pa_name AS patientName, dept_name AS deptName, doctor AS empName FROM no_medical) charge ORDER BY charge.payDate
